import React, { useContext } from 'react'
import Button from '../../UI/Button'
import EqualsButton from '../../UI/EqualsButton'
import classes from './InputBox.module.css'
import CartContext from '../../store/calculator-context'
import CalculatorContext from '../../store/calculator-context'

function InputBox() {

    let dummyArray = [1,2,3]
    let bottomArray = dummyArray.map(item => {
        return(
            <li>
                <Button buttonType = "number" className = {classes.number} value = {item}>{item}</Button>
            </li>
        )
    })

    let middleArray = dummyArray.map(item => {
        return(
            <li>
                <Button buttonType = "number" className = {classes.number} value = {item}>{item+3}</Button>
            </li>
        )
    })

    let topArray = dummyArray.map(item => {
        return(
            <li>
                <Button buttonType = "number" className = {classes.number} value = {item+6}>{item+6}</Button>
            </li>
        )
    })

    let calcCtx = useContext(CalculatorContext)

    console.log(calcCtx)

    return(
        <div className = {classes['input-box']}>
            <ul className = {classes['top-row']}>
                {topArray}
            </ul>

            <ul className = {classes['middle-row']}>
                {middleArray}
            </ul>

            <ul className = {classes['bottom-row']}>
                {bottomArray}
            </ul>
            <EqualsButton />
            <Button buttonType = "clear">C</Button>
        </div>
    )
}

export default InputBox
